;	// console.log(mainItemTotal);	function minutes_to_hhmm (numberOfMinutes) {	    //create duration object from moment.duration  	    var duration = moment.duration(numberOfMinutes, 'minutes');	    	    //calculate hours	    var hh = (duration.years()*(365*24)) + (duration.months()*(30*24)) + (duration.days()*24) + (duration.hours());	    	    //get minutes	    var mm = duration.minutes();	    	    //return total time in hh:mm format	    return hh+':'+mm;	}	var totalT = 0;	$('.indi-time').each( function( index) {			let indiTime = $(this).text();			totalT += parseInt( $(this).data('time') );			$(this).parents('table').prev().find('.indi-time-header').text( 'Completion Time: ' + indiTime );			$('.tct > span').text( minutes_to_hhmm( totalT ) + ' Hours');	} );	// Warning	var tlPrice = parseFloat($('.hnd-all-total').text());	// console.log(tlPrice);	if (tlPrice < 95.00) {		$('.hnd-cart-warning').show();	} else {		$('.hnd-cart-warning').hide();	}</script><script>	function print_func() {		window.print();	}	let timingSelector = document.querySelector('.arrivalq');	let timing = timingSelector.innerHTML.split(' - ');	let newTiming = new Array();	timing.forEach(function(i){	    let t = moment(i, 'HH:mm A').format('hh:mm A');	    newTiming.push(t);	});	timingSelector.innerHTML = newTiming.join(' - ');</script>